import axios from 'axios';
export default {
  name: 'App',
  data() {
    return {
      selectedFile: null,
      isDragging: false,
      isProcessing: false,
      correctedFile: null,
      errorMessage: '',
      showPreviewModal: false,
      compareMode: false,
      previewTitle: '',
      currentPage: 1,
      originalPage: 1,
      correctedPage: 1,
      totalPages: 0,
      pdfDoc: null,
      correctedPdfDoc: null,
      pdfLib: null
    };
  },
  mounted() {
    // 添加错误处理
    window.addEventListener('unhandledrejection', event => {
      if (event.reason && event.reason.message) {
        if (event.reason.message.includes('pdf') || event.reason.message.includes('private member')) {
          console.error('PDF相关未处理错误:', event.reason);
          this.errorMessage = 'PDF处理错误: ' + event.reason.message;
        }
      }
    });
    this.loadPdfJs();
  },
  methods: {
    async loadPdfJs() {
      try {
        // 检查是否已经加载
        if (window['pdfjs-dist/build/pdf'] && window['pdfjs-dist/build/pdf'].getDocument) {
          this.pdfLib = window['pdfjs-dist/build/pdf'];
          this.pdfLib.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.worker.min.js';
          return;
        }
        const script = document.createElement('script');
        // 使用更稳定的版本
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.min.js';
        script.onload = () => {
          try {
            this.pdfLib = window['pdfjs-dist/build/pdf'];
            this.pdfLib.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.worker.min.js';
          } catch (e) {
            console.error('PDF.js 初始化失败:', e);
            this.errorMessage = 'PDF.js 初始化失败: ' + e.message;
          }
        };
        script.onerror = error => {
          console.error('PDF.js 加载失败:', error);
          this.errorMessage = 'PDF.js 加载失败: 网络错误或资源不可用';
        };
        document.head.appendChild(script);
      } catch (error) {
        console.error('加载PDF.js时发生错误:', error);
        this.errorMessage = '加载PDF库时发生错误: ' + error.message;
      }
    },
    handleFileSelect(event) {
      const file = event.target.files[0];
      if (file && file.type === 'application/pdf') {
        this.selectedFile = file;
        this.errorMessage = '';
      } else {
        this.errorMessage = '请选择PDF文件';
      }
    },
    handleDrop(event) {
      this.isDragging = false;
      const file = event.dataTransfer.files[0];
      if (file && file.type === 'application/pdf') {
        this.selectedFile = file;
        this.errorMessage = '';
      } else {
        this.errorMessage = '请选择PDF文件';
      }
    },
    removeFile() {
      this.selectedFile = null;
      this.$refs.fileInput.value = '';
    },
    formatFileSize(bytes) {
      if (bytes === 0) return '0 Bytes';
      const k = 1024;
      const sizes = ['Bytes', 'KB', 'MB', 'GB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
    },
    async showPreview() {
      if (!this.pdfLib) {
        this.errorMessage = 'PDF预览库加载中，请稍候...';
        // 尝试重新加载
        this.loadPdfJs();
        return;
      }
      this.compareMode = false;
      this.previewTitle = '原始PDF预览';
      this.showPreviewModal = true;
      this.currentPage = 1;
      await this.$nextTick();
      await this.loadPdfForPreview(this.selectedFile, 'single');
    },
    async showCompareView() {
      if (!this.pdfLib) {
        this.errorMessage = 'PDF预览库加载中，请稍候...';
        // 尝试重新加载
        this.loadPdfJs();
        return;
      }

      // 确保已处理文件
      if (!this.correctedFile) {
        this.errorMessage = '请先处理文件再进行对比预览';
        return;
      }
      this.compareMode = true;
      this.previewTitle = '校正前后对比';
      this.showPreviewModal = true;
      this.originalPage = 1;
      this.correctedPage = 1;
      await this.$nextTick();
      try {
        await Promise.all([this.loadPdfForPreview(this.selectedFile, 'original'), this.loadCorrectedPdf()]);
      } catch (error) {
        console.error('对比预览加载失败:', error);
        this.errorMessage = '对比预览加载失败: ' + error.message;
      }
    },
    async loadPdfForPreview(file, canvasType) {
      try {
        // 检查文件是否存在
        if (!file) {
          throw new Error('未选择文件');
        }
        const arrayBuffer = await file.arrayBuffer();

        // 检查数据是否有效
        if (!arrayBuffer || arrayBuffer.byteLength === 0) {
          throw new Error('文件数据为空');
        }

        // 确保PDF库已加载
        if (!this.pdfLib || !this.pdfLib.getDocument) {
          throw new Error('PDF库未正确加载');
        }

        // 使用新的加载方式避免私有成员访问问题
        const loadingTask = this.pdfLib.getDocument({
          data: arrayBuffer
        });
        const pdf = await loadingTask.promise;
        if (canvasType === 'original') {
          // 清理之前的PDF对象
          if (this.pdfDoc) {
            try {
              this.pdfDoc.destroy();
            } catch (e) {
              console.warn('清理之前的PDF对象时出错:', e);
            }
          }
          this.pdfDoc = pdf;
        } else if (canvasType === 'single') {
          // 清理之前的PDF对象
          if (this.pdfDoc) {
            try {
              this.pdfDoc.destroy();
            } catch (e) {
              console.warn('清理之前的PDF对象时出错:', e);
            }
          }
          this.pdfDoc = pdf;
        }
        this.totalPages = pdf.numPages;
        if (canvasType === 'original') {
          await this.renderPage(this.originalPage, 'original');
        } else if (canvasType === 'single') {
          await this.renderPage(this.currentPage, 'single');
        }
      } catch (error) {
        console.error('PDF加载失败:', error);
        this.errorMessage = 'PDF加载失败: ' + (error.message || '未知错误');
      }
    },
    async loadCorrectedPdf() {
      try {
        // 检查文件名
        if (!this.correctedFile) {
          throw new Error('校正后的文件名为空');
        }
        const response = await axios.get(`http://localhost:8080/api/pdf/download/${this.correctedFile}`, {
          responseType: 'arraybuffer'
        });

        // 检查响应数据
        if (!response.data) {
          throw new Error('服务器返回空数据');
        }

        // 确保PDF库已加载
        if (!this.pdfLib || !this.pdfLib.getDocument) {
          throw new Error('PDF库未正确加载');
        }

        // 使用新的加载方式避免私有成员访问问题
        const loadingTask = this.pdfLib.getDocument({
          data: response.data
        });
        const pdf = await loadingTask.promise;

        // 清理之前的PDF对象
        if (this.correctedPdfDoc) {
          try {
            this.correctedPdfDoc.destroy();
          } catch (e) {
            console.warn('清理之前的校正PDF对象时出错:', e);
          }
        }
        this.correctedPdfDoc = pdf;
        await this.renderPage(this.correctedPage, 'corrected');
      } catch (error) {
        console.error('校正PDF加载失败:', error);
        this.errorMessage = '校正PDF加载失败: ' + (error.message || '未知错误');
      }
    },
    async renderPage(pageNum, canvasType) {
      let pdf, canvas;
      if (canvasType === 'original') {
        pdf = this.pdfDoc;
        canvas = this.$refs.originalCanvas;
      } else if (canvasType === 'corrected') {
        pdf = this.correctedPdfDoc;
        canvas = this.$refs.correctedCanvas;
      } else {
        pdf = this.pdfDoc;
        canvas = this.$refs.previewCanvas;
      }
      if (!pdf || !canvas) {
        console.warn('缺少PDF文档或Canvas元素:', {
          pdf: !!pdf,
          canvas: !!canvas,
          canvasType
        });
        return;
      }
      try {
        // 确保PDF对象有效
        if (typeof pdf.getPage !== 'function') {
          throw new Error('PDF对象无效');
        }

        // 获取页面前检查页码
        if (pageNum < 1 || pageNum > pdf.numPages) {
          throw new Error(`页码超出范围: ${pageNum} (总页数: ${pdf.numPages})`);
        }
        const page = await pdf.getPage(pageNum);
        const viewport = page.getViewport({
          scale: 1.5
        });
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;

        // 渲染前检查上下文和视口
        if (!context || !viewport) {
          throw new Error('Canvas上下文或视口无效');
        }
        const renderContext = {
          canvasContext: context,
          viewport: viewport
        };
        const renderTask = page.render(renderContext);
        await renderTask.promise;
      } catch (error) {
        console.error('页面渲染失败:', error);
        this.errorMessage = '页面渲染失败: ' + (error.message || '未知错误');
      }
    },
    async nextPage(pageType) {
      if (pageType === 'single') {
        if (this.currentPage < this.totalPages) {
          this.currentPage++;
          await this.renderPage(this.currentPage, 'single');
        }
      } else if (pageType === 'original') {
        if (this.originalPage < this.totalPages) {
          this.originalPage++;
          await this.renderPage(this.originalPage, 'original');
        }
      } else if (pageType === 'corrected') {
        if (this.correctedPage < this.totalPages) {
          this.correctedPage++;
          await this.renderPage(this.correctedPage, 'corrected');
        }
      }
    },
    async prevPage(pageType) {
      if (pageType === 'single') {
        if (this.currentPage > 1) {
          this.currentPage--;
          await this.renderPage(this.currentPage, 'single');
        }
      } else if (pageType === 'original') {
        if (this.originalPage > 1) {
          this.originalPage--;
          await this.renderPage(this.originalPage, 'original');
        }
      } else if (pageType === 'corrected') {
        if (this.correctedPage > 1) {
          this.correctedPage--;
          await this.renderPage(this.correctedPage, 'corrected');
        }
      }
    },
    closePreview() {
      this.showPreviewModal = false;
      // 正确清理PDF对象
      if (this.pdfDoc) {
        try {
          // 释放PDF文档资源
          if (this.pdfDoc.destroy) {
            this.pdfDoc.destroy();
          }
        } catch (e) {
          console.warn('释放PDF文档资源时出错:', e);
        }
        this.pdfDoc = null;
      }
      if (this.correctedPdfDoc) {
        try {
          // 释放校正PDF文档资源
          if (this.correctedPdfDoc.destroy) {
            this.correctedPdfDoc.destroy();
          }
        } catch (e) {
          console.warn('释放校正PDF文档资源时出错:', e);
        }
        this.correctedPdfDoc = null;
      }
    },
    async uploadAndCorrect() {
      if (!this.selectedFile) {
        this.errorMessage = '未选择文件';
        return;
      }
      this.isProcessing = true;
      this.errorMessage = '';
      const formData = new FormData();
      formData.append('file', this.selectedFile);
      try {
        const response = await axios.post('http://localhost:8080/api/pdf/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.success) {
          this.correctedFile = response.data.fileName;
        } else {
          this.errorMessage = response.data.message || '处理失败';
        }
      } catch (error) {
        console.error('文件上传失败:', error);
        this.errorMessage = '上传失败: ' + (error.response?.data?.message || error.message || '未知错误');
      } finally {
        this.isProcessing = false;
      }
    },
    downloadFile() {
      if (!this.correctedFile) return;
      const url = `http://localhost:8080/api/pdf/download/${this.correctedFile}`;
      const link = document.createElement('a');
      link.href = url;
      link.download = this.correctedFile;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    },
    reset() {
      this.selectedFile = null;
      this.correctedFile = null;
      this.errorMessage = '';
      this.$refs.fileInput.value = '';
    }
  }
};
