import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import axios from 'axios';
export default {
  name: 'App',
  data() {
    return {
      selectedFile: null,
      isDragging: false,
      isProcessing: false,
      correctedFile: null,
      errorMessage: '',
      showPreviewModal: false,
      compareMode: false,
      previewTitle: '',
      currentPage: 1,
      originalPage: 1,
      correctedPage: 1,
      totalPages: 0,
      pdfDoc: null,
      correctedPdfDoc: null,
      pdfLib: null
    };
  },
  mounted() {
    // 添加错误处理
    const handleUnhandledRejection = event => {
      if (event.reason && event.reason.message) {
        if (event.reason.message.includes('pdf') || event.reason.message.includes('private member')) {
          console.error('PDF相关未处理错误:', event.reason);
          this.errorMessage = 'PDF处理错误: ' + event.reason.message;
          // 特别处理私有成员错误
          if (event.reason.message.includes('private member')) {
            this.errorMessage = 'PDF引擎错误，请刷新页面重试';
          }
        }
      }
    };
    window.addEventListener('unhandledrejection', handleUnhandledRejection);

    // 添加一个全局重置PDF库的方法
    window.resetPdfViewer = () => {
      // 清理所有PDF对象
      if (this.pdfDoc && typeof this.pdfDoc.destroy === 'function') {
        try {
          this.pdfDoc.destroy();
        } catch (e) {
          console.warn('销毁pdfDoc时出错:', e);
        }
      }
      if (this.correctedPdfDoc && typeof this.correctedPdfDoc.destroy === 'function') {
        try {
          this.correctedPdfDoc.destroy();
        } catch (e) {
          console.warn('销毁correctedPdfDoc时出错:', e);
        }
      }
      this.pdfDoc = null;
      this.correctedPdfDoc = null;
      this.pdfLib = null;

      // 重置canvas
      const canvases = [this.$refs.originalCanvas, this.$refs.correctedCanvas, this.$refs.previewCanvas];
      canvases.forEach(canvas => {
        if (canvas) {
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
        }
      });

      // 重新加载
      this.loadPdfJs();
      console.log('PDF查看器已重置');
    };
    this.loadPdfJs();
  },
  beforeUnmount() {
    // 组件销毁时移除事件监听器
    window.removeEventListener('unhandledrejection', handleUnhandledRejection);
  },
  methods: {
    async loadPdfJs() {
      try {
        // 完全清理之前的PDF库
        if (window['pdfjs-dist/build/pdf']) {
          delete window['pdfjs-dist/build/pdf'];
        }

        // 使用传统script标签方式加载，确保兼容性
        return new Promise((resolve, reject) => {
          // 移除可能存在的旧脚本
          const existingScripts = document.querySelectorAll('script[data-pdfjs]');
          existingScripts.forEach(script => script.remove());
          const script = document.createElement('script');
          script.src = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.min.js';
          script.setAttribute('data-pdfjs', 'true');
          script.onload = () => {
            try {
              if (window['pdfjs-dist/build/pdf'] && window['pdfjs-dist/build/pdf'].getDocument) {
                this.pdfLib = window['pdfjs-dist/build/pdf'];
                this.pdfLib.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.4.120/pdf.worker.min.js';
                console.log('PDF.js 加载成功');
                resolve();
              } else {
                throw new Error('PDF.js 加载完成但对象不可用');
              }
            } catch (e) {
              console.error('PDF.js 初始化失败:', e);
              this.errorMessage = 'PDF.js 初始化失败: ' + e.message;
              reject(e);
            }
          };
          script.onerror = error => {
            console.error('PDF.js 加载失败:', error);
            this.errorMessage = 'PDF.js 加载失败: 网络错误或资源不可用';
            reject(error);
          };
          document.head.appendChild(script);
        });
      } catch (error) {
        console.error('加载PDF.js时发生错误:', error);
        this.errorMessage = '加载PDF库时发生错误: ' + error.message;
        throw error;
      }
    },
    handleFileSelect(event) {
      const file = event.target.files[0];
      if (file && file.type === 'application/pdf') {
        this.selectedFile = file;
        this.errorMessage = '';
      } else {
        this.errorMessage = '请选择PDF文件';
      }
    },
    handleDrop(event) {
      this.isDragging = false;
      const file = event.dataTransfer.files[0];
      if (file && file.type === 'application/pdf') {
        this.selectedFile = file;
        this.errorMessage = '';
      } else {
        this.errorMessage = '请选择PDF文件';
      }
    },
    removeFile() {
      this.selectedFile = null;
      this.$refs.fileInput.value = '';
    },
    formatFileSize(bytes) {
      if (bytes === 0) return '0 Bytes';
      const k = 1024;
      const sizes = ['Bytes', 'KB', 'MB', 'GB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
    },
    async showPreview() {
      if (!this.pdfLib) {
        this.errorMessage = 'PDF预览库加载中，请稍候...';
        // 尝试重新加载
        await this.loadPdfJs();
        // 等待一段时间再检查
        await new Promise(resolve => setTimeout(resolve, 1000));
        if (!this.pdfLib) {
          this.errorMessage = 'PDF预览库加载失败，请刷新页面重试';
          return;
        }
      }
      this.compareMode = false;
      this.previewTitle = '原始PDF预览';
      this.showPreviewModal = true;
      this.currentPage = 1;
      await this.$nextTick();
      await this.loadPdfForPreview(this.selectedFile, 'single');
    },
    async showCompareView() {
      if (!this.pdfLib) {
        this.errorMessage = 'PDF预览库加载中，请稍候...';
        // 尝试重新加载
        await this.loadPdfJs();
        // 等待一段时间再检查
        await new Promise(resolve => setTimeout(resolve, 1000));
        if (!this.pdfLib) {
          this.errorMessage = 'PDF预览库加载失败，请刷新页面重试';
          return;
        }
      }

      // 确保已处理文件
      if (!this.correctedFile) {
        this.errorMessage = '请先处理文件再进行对比预览';
        return;
      }
      this.compareMode = true;
      this.previewTitle = '校正前后对比';
      this.showPreviewModal = true;
      this.originalPage = 1;
      this.correctedPage = 1;
      await this.$nextTick();
      try {
        // 顺序加载，避免并发问题
        await this.loadPdfForPreview(this.selectedFile, 'original');
        await this.loadCorrectedPdf();
      } catch (error) {
        console.error('对比预览加载失败:', error);
        this.errorMessage = '对比预览加载失败: ' + error.message;
      }
    },
    async loadPdfForPreview(file, canvasType) {
      try {
        // 检查文件是否存在
        if (!file) {
          throw new Error('未选择文件');
        }

        // 确保PDF库已加载
        if (!this.pdfLib || !this.pdfLib.getDocument) {
          this.errorMessage = 'PDF库正在加载中...';
          await this.loadPdfJs();

          // 等待一段时间确保库完全初始化
          await new Promise(resolve => setTimeout(resolve, 500));
          if (!this.pdfLib || !this.pdfLib.getDocument) {
            throw new Error('PDF库加载失败，请刷新页面重试');
          }
        }
        const arrayBuffer = await file.arrayBuffer();

        // 检查数据是否有效
        if (!arrayBuffer || arrayBuffer.byteLength === 0) {
          throw new Error('文件数据为空');
        }

        // 创建新的loading task
        const loadingTask = this.pdfLib.getDocument({
          data: arrayBuffer
        });

        // 等待加载完成
        const pdf = await loadingTask.promise;

        // 清理之前的PDF对象
        if (canvasType === 'original' || canvasType === 'single') {
          if (this.pdfDoc) {
            try {
              if (typeof this.pdfDoc.destroy === 'function') {
                this.pdfDoc.destroy();
              }
            } catch (e) {
              console.warn('清理之前的PDF对象时出错:', e);
            }
          }
        }
        if (canvasType === 'original') {
          this.pdfDoc = pdf;
        } else if (canvasType === 'single') {
          this.pdfDoc = pdf;
        }
        this.totalPages = pdf.numPages;
        console.log(`PDF加载成功，共${this.totalPages}页`);
        if (canvasType === 'original') {
          await this.renderPage(this.originalPage, 'original');
        } else if (canvasType === 'single') {
          await this.renderPage(this.currentPage, 'single');
        }
      } catch (error) {
        console.error('PDF加载失败:', error);
        // 特别处理私有成员错误
        if (error.message && (error.message.includes('private member') || error.message.includes('Destroyed'))) {
          this.errorMessage = 'PDF引擎需要重置，请刷新页面或点击重置按钮';
        } else {
          this.errorMessage = 'PDF加载失败: ' + (error.message || '未知错误');
        }
      }
    },
    async loadCorrectedPdf() {
      try {
        // 检查文件名
        if (!this.correctedFile) {
          throw new Error('校正后的文件名为空');
        }

        // 确保PDF库已加载
        if (!this.pdfLib || !this.pdfLib.getDocument) {
          // 尝试重新加载
          await new Promise(resolve => {
            this.loadPdfJs();
            setTimeout(resolve, 1000); // 等待库加载
          });

          // 再次检查
          if (!this.pdfLib || !this.pdfLib.getDocument) {
            throw new Error('PDF库加载失败，请刷新页面重试');
          }
        }
        const response = await axios.get(`http://localhost:8080/api/pdf/download/${this.correctedFile}`, {
          responseType: 'arraybuffer',
          timeout: 30000 // 30秒超时
        });

        // 检查响应数据
        if (!response.data) {
          throw new Error('服务器返回空数据');
        }

        // 使用新的加载方式避免私有成员访问问题
        const loadingTask = this.pdfLib.getDocument({
          data: response.data,
          verbosity: this.pdfLib.VerbosityLevel.ERRORS
        });

        // 添加加载超时
        const timeoutPromise = new Promise((_, reject) => {
          setTimeout(() => reject(new Error('PDF加载超时')), 10000);
        });
        const pdf = await Promise.race([loadingTask.promise, timeoutPromise]);

        // 清理之前的PDF对象
        if (this.correctedPdfDoc) {
          try {
            if (typeof this.correctedPdfDoc.destroy === 'function') {
              this.correctedPdfDoc.destroy();
            }
          } catch (e) {
            console.warn('清理之前的校正PDF对象时出错:', e);
          }
        }
        this.correctedPdfDoc = pdf;
        await this.renderPage(this.correctedPage, 'corrected');
      } catch (error) {
        console.error('校正PDF加载失败:', error);
        // 特别处理私有成员错误
        if (error.message && error.message.includes('private member')) {
          this.errorMessage = 'PDF加载引擎错误，请刷新页面重试';
          // 尝试重新加载PDF库
          this.loadPdfJs();
        } else {
          this.errorMessage = '校正PDF加载失败: ' + (error.message || '未知错误');
        }
      }
    },
    async renderPage(pageNum, canvasType) {
      let pdf, canvas;
      if (canvasType === 'original') {
        pdf = this.pdfDoc;
        canvas = this.$refs.originalCanvas;
      } else if (canvasType === 'corrected') {
        pdf = this.correctedPdfDoc;
        canvas = this.$refs.correctedCanvas;
      } else {
        pdf = this.pdfDoc;
        canvas = this.$refs.previewCanvas;
      }
      if (!pdf || !canvas) {
        console.warn('缺少PDF文档或Canvas元素:', {
          pdf: !!pdf,
          canvas: !!canvas,
          canvasType
        });
        return;
      }
      try {
        // 确保PDF对象有效
        if (typeof pdf.getPage !== 'function') {
          throw new Error('PDF对象无效');
        }

        // 获取页面前检查页码
        if (pageNum < 1 || pageNum > pdf.numPages) {
          throw new Error(`页码超出范围: ${pageNum} (总页数: ${pdf.numPages})`);
        }
        const page = await pdf.getPage(pageNum);
        const viewport = page.getViewport({
          scale: 1.0
        });
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;
        context.clearRect(0, 0, canvas.width, canvas.height);

        // 使用更安全的渲染方式
        const renderContext = {
          canvasContext: context,
          viewport: viewport
        };
        const renderTask = page.render(renderContext);

        // 添加超时机制
        let timeoutId;
        const timeoutPromise = new Promise((_, reject) => {
          timeoutId = setTimeout(() => reject(new Error('渲染超时')), 10000);
        });
        await Promise.race([renderTask.promise, timeoutPromise]);
        clearTimeout(timeoutId);
      } catch (error) {
        console.error('页面渲染失败:', error);
        // 特别处理私有成员错误
        if (error.message && error.message.includes('private member')) {
          this.errorMessage = 'PDF渲染引擎错误，请刷新页面重试';
          // 尝试重新加载PDF库
          setTimeout(() => {
            this.loadPdfJs();
          }, 100);
        } else if (error.message && error.message.includes('Destroyed')) {
          this.errorMessage = 'PDF文档已被销毁，请重新打开文件';
        } else {
          this.errorMessage = '页面渲染失败: ' + (error.message || '未知错误');
        }
      }
    },
    async nextPage(pageType) {
      if (pageType === 'single') {
        if (this.currentPage < this.totalPages) {
          this.currentPage++;
          await this.renderPage(this.currentPage, 'single');
        }
      } else if (pageType === 'original') {
        if (this.originalPage < this.totalPages) {
          this.originalPage++;
          await this.renderPage(this.originalPage, 'original');
        }
      } else if (pageType === 'corrected') {
        if (this.correctedPage < this.totalPages) {
          this.correctedPage++;
          await this.renderPage(this.correctedPage, 'corrected');
        }
      }
    },
    async prevPage(pageType) {
      if (pageType === 'single') {
        if (this.currentPage > 1) {
          this.currentPage--;
          await this.renderPage(this.currentPage, 'single');
        }
      } else if (pageType === 'original') {
        if (this.originalPage > 1) {
          this.originalPage--;
          await this.renderPage(this.originalPage, 'original');
        }
      } else if (pageType === 'corrected') {
        if (this.correctedPage > 1) {
          this.correctedPage--;
          await this.renderPage(this.correctedPage, 'corrected');
        }
      }
    },
    closePreview() {
      this.showPreviewModal = false;
      // 正确清理PDF对象
      if (this.pdfDoc) {
        try {
          // 释放PDF文档资源
          if (this.pdfDoc.destroy && typeof this.pdfDoc.destroy === 'function') {
            this.pdfDoc.destroy();
          }
        } catch (e) {
          console.warn('释放PDF文档资源时出错:', e);
        }
        this.pdfDoc = null;
      }
      if (this.correctedPdfDoc) {
        try {
          // 释放校正PDF文档资源
          if (this.correctedPdfDoc.destroy && typeof this.correctedPdfDoc.destroy === 'function') {
            this.correctedPdfDoc.destroy();
          }
        } catch (e) {
          console.warn('释放校正PDF文档资源时出错:', e);
        }
        this.correctedPdfDoc = null;
      }

      // 重置canvas
      const canvases = [this.$refs.originalCanvas, this.$refs.correctedCanvas, this.$refs.previewCanvas];
      canvases.forEach(canvas => {
        if (canvas) {
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
        }
      });
    },
    async uploadAndCorrect() {
      if (!this.selectedFile) {
        this.errorMessage = '未选择文件';
        return;
      }
      this.isProcessing = true;
      this.errorMessage = '';
      const formData = new FormData();
      formData.append('file', this.selectedFile);
      try {
        const response = await axios.post('http://localhost:8080/api/pdf/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.success) {
          this.correctedFile = response.data.fileName;
        } else {
          this.errorMessage = response.data.message || '处理失败';
        }
      } catch (error) {
        console.error('文件上传失败:', error);
        this.errorMessage = '上传失败: ' + (error.response?.data?.message || error.message || '未知错误');
      } finally {
        this.isProcessing = false;
      }
    },
    downloadFile() {
      if (!this.correctedFile) return;
      const url = `http://localhost:8080/api/pdf/download/${this.correctedFile}`;
      const link = document.createElement('a');
      link.href = url;
      link.download = this.correctedFile;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    },
    reset() {
      this.selectedFile = null;
      this.correctedFile = null;
      this.errorMessage = '';
      this.$refs.fileInput.value = '';
    }
  }
};
